package itp341.barbosa.marlon.musicoftroy.model;




import java.io.BufferedReader;
import java.io.FileNotFoundException;
import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.io.OutputStream;
import java.io.OutputStreamWriter;
import java.io.Writer;
import java.text.ParseException;
import java.util.ArrayList;

import org.json.JSONArray;
import org.json.JSONException;
import org.json.JSONObject;
import org.json.JSONTokener;

import android.content.Context;

public class ArtistLoader {

	
	private static final String FILENAME = "artists.json";
	
	public static ArrayList<Artist> loadArtists() {
		ArrayList<Artist> artists = new ArrayList<Artist>();
		
		
		artists.add(new Artist("John Lennon", "English musician, singer and songwriter who rose to worldwide fame as a founder member of the rock band the Beatles, the most commercially successful band in the history of popular music. ", ""));
		artists.add(new Artist("Ringo Starr", "English musician, singer, songwriter, and actor who gained worldwide fame as the drummer for the Beatles.", ""));
		artists.add(new Artist("George Harrison", "English musician, multi-instrumentalist, singer and songwriter who achieved international fame as the lead guitarist of the Beatles.", ""));
		artists.add(new Artist("Paul McCartney", "English musician, singer, songwriter, multi-instrumentalist, and composer.", ""));
		
		return artists;	
	}
	
	public static ArrayList<Artist> loadArtistsFromJSON(String jsonString) throws JSONException, ParseException {
		ArrayList<Artist> artists = new ArrayList<Artist>();
		
		JSONArray array;

			array = (JSONArray) new JSONTokener(
					jsonString).nextValue();
			for (int i = 0; i < array.length(); i++) {
				JSONObject json = array.getJSONObject(i);
				artists.add(new Artist(json));
			}
		
		
		return artists;
	}

	public static ArrayList<Artist> loadArtistsFromJSON(Context c) {
		ArrayList<Artist> artists = new ArrayList<Artist>();
		BufferedReader reader = null;
		try {
			InputStream in = c.openFileInput(FILENAME);
			if (in != null) {
				reader = new BufferedReader(new InputStreamReader(in));
				StringBuilder jsonString = new StringBuilder();
				String line = null;
				while ((line = reader.readLine()) != null) {
					jsonString.append(line);

				}
				JSONArray array = (JSONArray) new JSONTokener(
						jsonString.toString()).nextValue();

				for (int i = 0; i < array.length(); i++) {
					JSONObject json = array.getJSONObject(i);
					artists.add(new Artist(json));
				}

				if (reader != null) {
					reader.close();
				}
			}
		} catch (FileNotFoundException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (JSONException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (ParseException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} 
			return artists;
		
	}
	
	
	public static void saveArtistsToJSON(Context c, ArrayList<Artist> artists) {
		JSONArray array = new JSONArray();
		for (Artist artist : artists) {
			try {
				array.put(artist.toJSON());
			} catch (JSONException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
		}

		Writer writer = null;
		OutputStream out;
		try {
			out = c.openFileOutput(FILENAME, Context.MODE_PRIVATE);
			writer = new OutputStreamWriter(out);
			writer.write(array.toString());
			writer.close();
		} catch (FileNotFoundException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}

	}
	
	
}
